/********************
 Animation
 ********************/

const requestAnimationFrameHandle =
    window.requestAnimationFrame ||
    window.mozRequestAnimationFrame ||
    window.webkitRequestAnimationFrame ||
    window.msRequestAnimationFrame

const cancelAnimationFrameHandle =
    window.cancelAnimationFrame ||
    Window.webkitCancelAnimationFrame ||
    window.mozCancelAnimationFrame ||
    window.msCancelAnimationFrame ||
    window.oCancelAnimationFrame

const windowAddEvent = window.addEventListener
const windowRemoveEvent = window.removeEventListener

export {
  requestAnimationFrameHandle,
  cancelAnimationFrameHandle,
  windowAddEvent,
  windowRemoveEvent
}
