<template>
  <bg_fire_expo ref="ch"/>
</template>

<script>
import Bg_fire_expo from "../components/bg_fire";
import {randIn} from "../engines/three/wrapengine/mathlib";

export default {
  name: "test_anichart",
  components: {Bg_fire_expo},
  mounted() {
    this.$nextTick(() => {
      let k = 0;
      const el = this.$refs.ch
      el.startJump()
      setInterval(() => {
        k += 0.03
        el.pushFactor(k, 0)
      }, 500)

      const end = randIn(4, 20);
      setTimeout(() => {
        el.kill()
      }, end * 1000)
    })
  }
}
</script>

