import { Text } from "pixi.js"
import Sprite from "./Sprite.js"

export default {
  mixins: [Sprite],
  props: {
    text: {
      type: String,
      required: false,
      default: ""
    }
  },
  computed: {
    instance: () => new Text()
  },
  watch: {
    instance: {
      handler(newInstance, oldInstance) {
        if (this.text) {
          newInstance.text = this.text
        }
      },
      immediate: true
    },
    text(text) {
      this.instance.text = text
    }
  }
}
